mapper.select:
select u.id,u.name,u.account,u.gender,f.bucket,f.file_name from sys_user u
left join sse_file f on u.avatar_id = f.id
where u.account = #{value} or u.name = #{value}

select u.id,u.name,u.account,u.gender,f.bucket,f.file_name from sys_user u
left join sse_file f on u.avatar_id = f.id
where u.id = #{value}

select u.id,u.name,u.account,u.password,u.gender,u.address,u.mobile,f.bucket,f.file_name from sys_user u
left join sse_file f on u.avatar_id = f.id
where u.account = #{value}

SELECT ur.id, ur.friend_id userId, g.id group_id, ur.remark_name, u.`name`, f.bucket, f.file_name, g.`name` groupName FROM `sys_friend_group` g LEFT JOIN sys_user_relations ur on g.id = ur.group_id LEFT JOIN sys_user u ON ur.friend_id = u.id LEFT JOIN sse_file f ON u.avatar_id = f.id WHERE g.create_user = #{userId}
and ur.group_id = #{groupId}
order by g.create_time
